      INSERT INTO GR_REQUEST(RE_CODE, NG_CODE, MEM_ID)
      VALUES(requestCodeSeq, #{ngCode}, #{memId});

      INSERT INTO PAYMENT(PAY_CODE, RE_CODE, PM_CODE, PAY_DETAIL)
